const EmojiPicker = ({ onSelect }) => {
    const [activeCategory, setActiveCategory] = useState('Trending ðŸ”¥'); // ðŸ†• Default to Trending
    const [searchTerm, setSearchTerm] = useState('');
    const [trendingEmojis, setTrendingEmojis] = useState([]); // ðŸ†• Trending emojis state

    // ðŸ†• Fetch trending emojis from API
    useEffect(() => {
        const fetchTrendingEmojis = async () => {
            try {
                const response = await fetch('/api/emoji/trending/');
                if (response.ok) {
                    const data = await response.json();
                    // API returns [{emoji: 'ðŸ˜€', count: 42}, ...]
                    const emojis = data.map(item => item.emoji);
                    setTrendingEmojis(emojis);
                    // Update EMOJI_CATEGORIES with trending
                    EMOJI_CATEGORIES['Trending ðŸ”¥'] = emojis;
                }
            } catch (error) {
                console.error('Failed to fetch trending emojis:', error);
                // Fallback to default trending
                const fallbackTrending = ['ðŸ”¥', 'â¤ï¸', 'ðŸ˜‚', 'ðŸ‘', 'ðŸŽ‰', 'ðŸ’¯', 'âœ¨', 'ðŸ˜', 'ðŸš€', 'ðŸ’ª'];
                setTrendingEmojis(fallbackTrending);
                EMOJI_CATEGORIES['Trending ðŸ”¥'] = fallbackTrending;
            }
        };

        fetchTrendingEmojis();
    }, []);

    const filteredEmojis = searchTerm
        ? Object.values(EMOJI_CATEGORIES).flat().filter(emoji => emoji.includes(searchTerm))
        : EMOJI_CATEGORIES[activeCategory] || [];

    return (
        <div style={styles.container}>
            {/* Search Bar */}
            <div style={styles.searchBar}>
                <input
                    type="text"
                    placeholder="Emoji ara..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    style={styles.searchInput}
                />
            </div>

            {/* Category Tabs */}
            {!searchTerm && (
                <div style={styles.categories}>
                    {Object.keys(EMOJI_CATEGORIES).map(category => (
                        <button
                            key={category}
                            onClick={() => setActiveCategory(category)}
                            style={{
                                ...styles.categoryButton,
                                ...(activeCategory === category && styles.activeCategoryButton)
                            }}
                        >
                            {category}
                        </button>
                    ))}
                </div>
            )}

            {/* Emoji Grid */}
            <div style={styles.emojiGrid}>
                {filteredEmojis.map((emoji, index) => (
                    <button
                        key={index}
                        onClick={() => onSelect(emoji)}
                        style={styles.emojiButton}
                        title={emoji}
                    >
                        {emoji}
                    </button>
                ))}
            </div>
        </div>
    );
};

